function addCircle(e) {
  for (var t = e,
  r = e,
  i = new THREE.Geometry,
  n = i.vertices,
  a = 2 * Math.PI / t,
  o = 0; t > o; o++) {
    var s = r * Math.cos(a * o),
    h = r * Math.sin(a * o);
    n.push(new THREE.Vector3(s, h, 0))
  }
  // i.computeLineDistances();
  var l = new THREE.LineBasicMaterial({
    color: 16777215,
    linewidth: 1,
    opacity: .4,
    transparent: true
  }),
  c = new THREE.Line(i, l);
  c.computeLineDistances()
  return c.position.y = -1,
  c.rotation.x = Math.PI / 2,
  c
}
function addParticlesCircle(e) {
  var t = new THREE.Geometry
  var r = new THREE.TextureLoader().load("img/spark.png")
  var i = new THREE.PointsMaterial({
    size: 5,
    map: r,
    blending: THREE.AdditiveBlending,
    depthWrite: false,
    depthTest: true,
    transparent: true,
    opacity: .9 * Math.random()
  })
  for (n = (40 * Math.random(), 0); 2e3 > n; n += 2) {
    var a = .175 * n + Math.PI,
    o = e * Math.sin(a) * Math.random() * 5,
    s = 30 * Math.random() - 15,
    h = e * Math.cos(a) * Math.random() * 5;
    t.vertices.push(new THREE.Vector3(o, s, h))
  }
  var l = new THREE.Points(t, i);
  return l
}
function makeTextSprite(e, t, r, i) {
  t = t || {}
  var n = t.hasOwnProperty("fontface") ? t.fontface: "Raleway",
  a = t.hasOwnProperty("fontsize") ? t.fontsize: 17,
  o = t.hasOwnProperty("borderThickness") ? t.borderThickness: 4,
  s = t.hasOwnProperty("borderColor") ? t.borderColor: {r: 10,g: 10,b: 20,a: 1},
  h = t.hasOwnProperty("backgroundColor") ? t.backgroundColor: {r: 255,g: 255,b: 255,a: 1},
  l = "new" == r ? {r: 254,g: 220,b: 180,a: 1}: {r: 255,g: 255,b: 255,a: .02},
  c = document.createElement("canvas");
  c.width = 800,
  c.height = 400;
  var u = c.getContext("2d");
  u.font = a + "px " + n,
  u.textBaseline = "top";
  u.fillStyle = "rgba(" + h.r + "," + h.g + "," + h.b + "," + h.a + ")"
  u.strokeStyle = "rgba(" + s.r + "," + s.g + "," + s.b + "," + s.a + ")"
  u.lineWidth = o
  u.fillStyle = "rgba(" + l.r + ", " + l.g + ", " + l.b + "," + i + ")"
  u.fillText(e, 435, 100);
  var d = new THREE.Texture(c);
  d.needsUpdate = true;
  var f = new THREE.SpriteMaterial({
    map: d
  }),
  E = new THREE.Sprite(f),
  m = 40;
  E.scale.set(.5 * m, .25 * m, .75 * m)
  return E
}
function onDocumentMouseDown(e) {
  e.preventDefault(),
  WebGLCanvas.addEventListener("mousemove", onDocumentMouseMove, false),
  WebGLCanvas.addEventListener("mouseup", onDocumentMouseUp, false),
  WebGLCanvas.addEventListener("mouseout", onDocumentMouseOut, false),
  mouseXOnMouseDown = e.clientX - windowHalfX,
  targetRotationOnMouseDown = targetRotation,
  isDrag = true,
  isIntro = false,
  mouse.x = e.clientX / window.innerWidth * 2 - 1,
  mouse.y = 2 * -(e.clientY / window.innerHeight) + 1;
}
function onDocumentMouseMove(e) {
  mouseX = e.clientX - windowHalfX,
  mouse.x = e.clientX / window.innerWidth * 2 - 1,
  mouse.y = 2 * -(e.clientY / window.innerHeight) + 1,
  targetRotation = targetRotationOnMouseDown + .02 * (mouseX - mouseXOnMouseDown),
  isDrag = true,
  isIntro = false
}
function onDocumentMouseUp() {
  WebGLCanvas.removeEventListener("mousemove", onDocumentMouseMove, false),
  WebGLCanvas.removeEventListener("mouseup", onDocumentMouseUp, false),
  WebGLCanvas.removeEventListener("mouseout", onDocumentMouseOut, false),
  isDrag = false
}
function onDocumentMouseOut() {
  WebGLCanvas.removeEventListener("mousemove", onDocumentMouseMove, false),
  WebGLCanvas.removeEventListener("mouseup", onDocumentMouseUp, false),
  WebGLCanvas.removeEventListener("mouseout", onDocumentMouseOut, false),
  isDrag = false
}
function onDocumentTouchStart(e) {
  if (2 === e.touches.length) {
    e.preventDefault(),
    mouse.x = e.touches[0].pageX / window.innerWidth * 2 - 1,
    mouse.y = 2 * -(e.touches[0].pageY / window.innerHeight) + 1;
  }
}
function onDocumentTouchMove(e) {
  1 === e.touches.length && (e.preventDefault(), mouseX = e.touches[0].pageX - windowHalfX, targetRotation = targetRotationOnMouseDown + .05 * (mouseX - mouseXOnMouseDown))
}
// 获取圆球数据
function callwishes_json() {
  var e = new XMLHttpRequest,
  t = 0;
  e.open("GET", "https://puge-10017157.cos.ap-shanghai.myqcloud.com/tempLong/get_wishes.json", true),
  e.setRequestHeader("Content-type", "application/json", true),
  e.onreadystatechange = function() {
    if (4 == e.readyState && 200 == e.status) {
      var r = JSON.parse(e.responseText),
      i = r.totalwishes,
      n = r.wishes;
      console.log(i + " wishes published - Drag the sphere to rotate - Click on stars to discover each wish")
      for (var a in n) {
        addWishes(t++, n[a].name + " (" + n[a].place + ")", n[a].wishtype)
      }
      // a = {
      //   type: n[a].wishtype,
      //   id: n[a].id,
      //   wish: "Hello 2015,<br> I wish to <br>" + n[a].wish + " <span>" + n[a].name + " (" + n[a].place + ")</span>"
      // },
      // a = null,
      
    }
  },
  e.send(null)
}
function setFormPos() {
  formwrapper.style.display = "block";
  var e = 550 > windowHalfX ? 170 : windowHalfX - 370;
  formwrapper.style.top = windowHalfY - formwrapper.offsetHeight + 20 + "px",
  formwrapper.style.right = e + "px",
  TweenLite.to(formwrapper, 1, {
    autoAlpha: 1,
    ease: Back.easeOut
  }),
  isIntro = false
}
function init() {
  mouse = new THREE.Vector2,
  scene = new THREE.Scene,
  renderer = new THREE.WebGLRenderer({
    antialias: true,
    alpha: true
  }),
  renderer.setSize(WIDTH, HEIGHT),
  renderer.sortObjects = false,
  WebGLCanvas.appendChild(renderer.domElement),
  camera = new THREE.PerspectiveCamera(400, window.innerWidth / window.innerHeight, 1, 2e3),
  camerapos.positionZ = camera.position.z = 150,
  camerapos.positionY = camera.position.y = 25,
  camerapos.positionX = 0,
  group = new THREE.Group,
  group2 = new THREE.Group,
  group3 = new THREE.Group,
  scene.add(group),
  scene.add(group2),
  scene.add(group3),
  raycaster = new THREE.Raycaster,
  light = new THREE.PointLight(16777215, 1.5),
  light.position.set(100, 100, 200),
  scene.add(light);
  var e = new THREE.HemisphereLight(2865844, 2865844, 1.25);
  e.color.setHex(15239532),
  e.groundColor.setHSL(.1, .8, .4),
  e.position.set( - 1, 1.75, 1),
  scene.add(e),
  document.addEventListener("mousemove", onDocumentMouseMove2, false),
  WebGLCanvas.addEventListener("mousedown", onDocumentMouseDown, false),
  WebGLCanvas.addEventListener("touchstart", onDocumentTouchStart, false),
  WebGLCanvas.addEventListener("touchmove", onDocumentTouchMove, false),
  window.addEventListener("resize", onWindowResize, false),
  controls = new THREE.OrbitControls(camera, renderer.domElement, renderer.domElement),
  controls.minDistance = 120,
  controls.maxDistance = 200,
  controls.maxPolarAngle = Math.PI / 2,
  controls.minPolarAngle = .8,
  addStarfield()
}
function loadCollada() {
  console.log("Loading...")
  var e = new THREE.ColladaLoader;
  e.load("img/island.dae", function(e) {
    planet = e.scene;
    // e.skins[0];
    TweenLite.to(planet.scale, 2, {
      x: 4,
      y: 4,
      z: 4,
      ease: Back.easeOut
    }),
    group.add(planet)
    addObjects()
    callwishes_json()
  })
}
// 添加星光
function addStarfield() {
  var e = addParticlesCircle(40);
  group3.add(e),
  TweenLite.to(e.material, 0, {
    opacity: 0
  }),
  TweenLite.to(e.material, 1, {
    opacity: .9 * Math.random(),
    delay: 2
  });
  var t = addParticlesCircle(60);
  group2.add(t),
  TweenLite.to(t.material, 0, {
    opacity: 0
  }),
  TweenLite.to(e.material, 1, {
    opacity: .9 * Math.random(),
    delay: 2
  });
  var r = addParticlesCircle(83);
  group3.add(r),
  TweenLite.to(r.material, 0, {
    opacity: 0
  }),
  TweenLite.to(e.material, 1, {
    opacity: .9 * Math.random(),
    delay: 2
  })
}
// 添加线以及光圈
function addObjects() {
  // 光圈
  var e = new THREE.SphereGeometry(40, 16, 16),
  t = new THREE.MeshBasicMaterial({
    color: 16777215,
    wireframe: false,
    transparent: true,
    opacity: .05
  });
  t.depthWrite = false;
  var r = new THREE.Mesh(e, t);
  group.add(r);

  // 文字
  new THREE.TextureLoader().load("img/text.png", texture => {
    var n = new THREE.SpriteMaterial({
      map: texture,
      color: 16777215,
      fog: true
    })
    var a = new THREE.Sprite(n);
    a.scale.set(45, 8, 45)
    a.rotation.copy(camera.rotation)
    a.translateY(25)
    a.updateMatrix()
    scene.add(a);
  });

  // 圆圈
  var o = addCircle(50);
  scene.add(o),
  o.scale.set(.2, .2, .2),
  TweenLite.to(o.scale, .7, {
    x: 1,
    y: 1,
    z: 1,
    ease: Back.easeOut,
    delay: .8
  });
  var s = addCircle(70);
  scene.add(s)

  // 圆圈
  s.scale.set(.2, .2, .2),
  TweenLite.to(s.scale, .7, {
    x: 1,
    y: 1,
    z: 1,
    ease: Back.easeOut,
    delay: .7
  });
  var h = addCircle(80);
  scene.add(h)
  
  // 圆圈
  h.scale.set(.2, .2, .2),
  TweenLite.to(h.scale, .7, {
    x: 1,
    y: 1,
    z: 1,
    ease: Back.easeOut,
    delay: .5
  });
  var l = addCircle(30);
  scene.add(l)
  
  l.scale.set(.2, .2, .2),
  TweenLite.to(l.scale, .7, {
    x: 1,
    y: 1,
    z: 1,
    ease: Back.easeOut,
    delay: .9
  }),
  TweenMax.delayedCall(5, setFormPos)
}
function addWishes(e, t, r) {
  var i = 1.2
  var n = (new THREE.SphereGeometry(i, 32, 16), 2 * e * .175 + Math.PI)
  
  const texture = new THREE.TextureLoader().load("img/b" + r + ".png")
  var o = 3 * Math.random() / 10 + .7
  var s = new THREE.SpriteMaterial({
    map: texture,
    color: 16777215,
    opacity: o,
    transparent: true
  });
  part = new THREE.Sprite(s),
  part.scale.set(2.5, 2.5, 2.5);
  var h = 70 * Math.sin(n),
  l = -(e / 2) + 20 * Math.random(),
  c = 70 * Math.cos(n);
  part.position.x = 0,
  part.position.y = 0,
  part.position.z = 0;
  var u = makeTextSprite(t, {}, "", o);
  u.position.y = -2,
  part.name = e,
  part.add(u)
  group2.add(part)
  objects.push(part)
  TweenLite.to(part.position, .6, {
    x: h,
    y: l,
    z: c,
    ease: Back.easeOut,
    delay: .9 + .1 * e
  })
}
function onWindowResize() {
  camera.aspect = window.innerWidth / window.innerHeight,
  camera.updateProjectionMatrix(),
  renderer.setSize(window.innerWidth, window.innerHeight),
  formwrapper.style.top = window.innerHeight / 2 - formwrapper.offsetHeight + 20 + "px";
  var e = window.innerWidth < 1100 ? 170 : window.innerWidth / 2 - 370;
  formwrapper.style.right = e + "px"
}
function onDocumentMouseMove2(e) {
  e.preventDefault(),
  mouse.x = e.clientX / window.innerWidth * 2 - 1,
  mouse.y = 2 * -(e.clientY / window.innerHeight) + 1
}
function animate() {
  requestAnimationFrame(animate),
  controls.update(),
  render()
}
function render() {
  if (isDrag) {
    (ecart = .1 * (targetRotation - group.rotation.y), stopTimer = false, setTimeout(function() {
      stopTimer = true
    },
    3e3), isIntro = false)
  } else {
    if (stopTimer) {
      ecart = -1 / 800
    } else {
      ecart = .1 * (targetRotation - group.rotation.y)
    }
  }
  isIntro || (group.rotation.y += ecart)
  group2.rotation.y += .7 * ecart
  group3.rotation.y += .9 * ecart
  camera.lookAt(scene.position);
  var e = new THREE.Vector3(mouse.x, mouse.y, 1).unproject(camera);
  raycaster.set(camera.position, e.sub(camera.position).normalize());
  // console.log(objects)
  // return
  // var t = raycaster.intersectObjects(objects);
  // t.length > 0 ? INTERSECTED != t[0].object && (INTERSECTED = t[0].object, WebGLCanvas.classList.add("cursoron")) : (WebGLCanvas.classList.remove("cursoron"), INTERSECTED = null)
  renderer.render(scene, camera);
  var r = 5e-4 * Date.now();
  light.position.x = 3e3 * Math.sin(4 * r),
  light.position.y = 1e3,
  light.position.z = 3e3 * Math.cos(4 * r)
}

var scene,
camera,
renderer,
raycaster,
light,
controls,
targetRotation = 0,
targetRotationOnMouseDown = 0,
island,
group,
group2,
group3,
part,
objects = [],
mouseX = 0,
mouseY = 0,
mouse,
INTERSECTED,
mouseXOnMouseDown = 0,
WebGLCanvas = document.getElementById("WebGLCanvas"),
WIDTH = window.innerWidth,
HEIGHT = window.innerHeight,
windowHalfX = WIDTH / 2,
windowHalfY = HEIGHT / 2,
isDrag = false,
isIntro = true,
stopTimer = true,
formwrapper = document.getElementById("formwrapper"),
ecart = 0,
camerapos = {}


window.onload = function() {
  if (Detector.webgl) {
    init()
    animate()
  } else {
    alert('不支持webGL')
  }
}

loadCollada()